const isNotNullOrUndefined=e=>null!=e&&"undefined"!==e,Main={accordionClick:()=>{const e=document.querySelectorAll(".js-accordion");isNotNullOrUndefined(e)&&e.forEach((t=>{const i=t.querySelector(".title");isNotNullOrUndefined(i)&&i.addEventListener("click",(function(){const n=t.classList.contains("active");e.forEach((e=>{e.classList.remove("active"),e.querySelector(".title").setAttribute("aria-expanded","false")})),n||(t.classList.add("active"),i.setAttribute("aria-expanded","true"))}))}))},init:()=>{Main.accordionClick()}};document.addEventListener("DOMContentLoaded",(function(){Main.init()}));